UserConnection = UserConnection.AppConnection.SystemUserConnection;
var esq = new EntitySchemaQuery(UserConnection.EntitySchemaManager, "Change");

string idColumnName = esq.AddColumn(esq.RootSchema.GetPrimaryColumnName()).Name;
var OwnerColumnName = esq.AddColumn("[Contact:Id:Owner].Name").Name;
var OwnerEmailColumnName = esq.AddColumn("[Contact:Id:Owner].Email").Name;// ФИО ответственного
esq.AddColumn("Owner");
esq.AddColumn("Number"); // Номер изменения
esq.AddColumn("ScheduledClosureDate"); // Плановый срок завершения реализации
esq.AddColumn("ScheduledStartDate"); // Плановый срок начала реализации
esq.AddColumn("ScheduledCustomerDate"); // Плановый срок приемки заказчиком
esq.AddColumn("ScheduledImplantationDate"); // Плановый срок внедрения
esq.AddColumn("PlannedDateCoordinationOfChange"); // Плановый срок согласования изменения

var esqFirstFilter = esq.CreateFilterWithParameters(
														FilterComparisonType.Less,
														"ScheduledClosureDate",
														CurrentDate
													);
var esqSecondFilter = esq.CreateFilterWithParameters(
														FilterComparisonType.Less,
														"ScheduledStartDate",
														CurrentDate
													);
var esqThirdFilter = esq.CreateFilterWithParameters(
														FilterComparisonType.Less,
														"ScheduledCustomerDate",
														CurrentDate
													);
var esqFourthFilter = esq.CreateFilterWithParameters(
														FilterComparisonType.Less,
														"ScheduledImplantationDate",
														CurrentDate
													);
var esqFifthFilter = esq.CreateFilterWithParameters(
														FilterComparisonType.Less,
														"PlannedDateCoordinationOfChange",
														CurrentDate
													);
esq.Filters.LogicalOperation = LogicalOperationStrict.Or;
esq.Filters.Add(esqFirstFilter);
esq.Filters.Add(esqSecondFilter);
esq.Filters.Add(esqThirdFilter);
esq.Filters.Add(esqFourthFilter);
esq.Filters.Add(esqFifthFilter);

var items = esq.GetEntityCollection(UserConnection);
if (items.Count > 0) {
	foreach(var item in items) {
		var ChangeId = item.GetTypedColumnValue<Guid>(idColumnName);
		var OwnerId = item.GetTypedColumnValue<Guid>("OwnerId");
		var OwnerName = item.GetTypedColumnValue<string>(OwnerColumnName);
		var OwnerEmail = item.GetTypedColumnValue<string>(OwnerEmailColumnName);
		var Number = item.GetTypedColumnValue<string>("Number");
		var ScheduledClosureDate = item.GetTypedColumnValue<DateTime>("ScheduledClosureDate");
		var ScheduledStartDate = item.GetTypedColumnValue<DateTime>("ScheduledStartDate");
		var ScheduledCustomerDate = item.GetTypedColumnValue<DateTime>("ScheduledCustomerDate");
		var ScheduledImplantationDate = item.GetTypedColumnValue<DateTime>("ScheduledImplantationDate");
		var PlannedDateCoordinationOfChange = item.GetTypedColumnValue<DateTime>("PlannedDateCoordinationOfChange");

		string MessageText = "Уважаемый " + OwnerName + ".\n Изменения №" + Number + " просрочены по следующим срокам:\n";
		if (DateTime.Compare(ScheduledClosureDate, CurrentDate) < 0 && ScheduledClosureDate != DateTime.MinValue) {
			MessageText += "Плановый срок завершения реализации - " + ScheduledClosureDate.ToString() + "\n";
		}
		if (DateTime.Compare(ScheduledStartDate, CurrentDate) < 0 && ScheduledStartDate != DateTime.MinValue) {
			MessageText += "Плановый срок начала реализации - " + ScheduledStartDate.ToString() + "\n";
		}
		if (DateTime.Compare(ScheduledCustomerDate, CurrentDate) < 0 && ScheduledCustomerDate != DateTime.MinValue) {
			MessageText += "Плановый срок приемки заказчиком - " + ScheduledCustomerDate.ToString() + "\n";
		}
		if (DateTime.Compare(ScheduledImplantationDate, CurrentDate) < 0 && ScheduledImplantationDate != DateTime.MinValue) {
			MessageText += "Плановый срок внедрения - " + ScheduledImplantationDate.ToString() + "\n";
		}
		if (DateTime.Compare(PlannedDateCoordinationOfChange, CurrentDate) < 0 && PlannedDateCoordinationOfChange != DateTime.MinValue) {
			MessageText += "Плановый срок согласования изменения - " + PlannedDateCoordinationOfChange.ToString() + "\n";
		}

		var processSchemaManager = (ProcessSchemaManager)UserConnection.GetSchemaManager(@"ProcessSchemaManager");
		var processSchema = processSchemaManager.GetInstanceByName(@"KmChangeViolationEmailProcess");
		var process = processSchema.CreateProcess(UserConnection);
		process.SetPropertyValue(@"MessageText", MessageText);
		process.SetPropertyValue(@"Recipient", OwnerName + " <" + OwnerEmail + ">");
		process.SetPropertyValue(@"ChangeId", OwnerId);
		process.SetPropertyValue(@"RecipientId", ChangeId);
		process.Execute(UserConnection);
	}
}
return true;
